function uploadFiles(form) {
  try {

    var dropbox = "Shift_Reqs"; // Input the name of the folder to be used for holding the uploaded files
    var folder, folders = DriveApp.getFoldersByName(dropbox);
    var filepath = "";
    var usrname = "";
    var usremail = "";

    if (folders.hasNext()) {
      folder = folders.next();
    } else {
      folder = DriveApp.createFolder(dropbox); // Otherwise by default a 'dropbox' folder will be created
    }

    var blob = form.inputFile;    
    var file = folder.createFile(blob);
    var out_str = '<b>File upload successfull!</b>';
    var out_str2 = '<b>  File path = </b>';   
    var out_str3 = '<b>   |   Thank you! Please confirm with us that we have your file(s). You can now close this tab.</b>';

    file.setDescription("Uploaded by " + form.inputName + ", Email: " + form.inputEmail);
    filepath = file.getUrl();
    usrname = form.inputName;
    usremail = form.inputEmail;
    return out_str.fontcolor("white") + out_str2.fontcolor("white") + file.getUrl().fontcolor("white") + '\n' + out_str3.fontcolor("white");
  }

  catch (error) {
    return error.toString();
  }  

  finally {
    MailApp.sendEmail("starcanyonschool@gmail.com",  // to field for the email. Can input multiple, ie: "1@email.com, 2@email.com",
    "Angelscare Staffing Form",       // from field
    "File upload by " + usrname,      // email subject  // + ", Email: " + usremail
    "You have a new file upload to review: " + filepath + "\n\nFrom: " + usrname + "\n\nEmail: " + usremail + "\n\nYou can view the file here: https://drive.google.com/drive/folders/1sC7BbIGwSsu-FnR4GGbof-m5vbxWc1Ci?usp=sharing");  // email body
  }
}

