function sendData(start_date, fname, lname, phone, email, version, ans) {
  //start_date, class_time, fname, lname, mname, email, phone, addr_street, addr_city, addr_zip
  var timestamp = Utilities.formatDate(new Date(), "America/Phoenix", "yyyy-MM-dd hh:mm a");
  var start = Utilities.formatDate(new Date(start_date.trim() + " 12:00:00"), "America/Phoenix", "MM-dd-yyyy");
  //var dob = Utilities.formatDate(new Date(birth_date.trim() + " 12:00:00"), "America/Phoenix", "MM/dd/yyyy");
  //var start = Utilities.formatDate(new Date(), "America/Phoenix", "MM-dd-yyyy");
  var my_app = SpreadsheetApp;
   //var my_book = my_app.getActiveSpreadsheet();
  var url = "https://docs.google.com/spreadsheets/d/1-eUN6iTBj9OInQYn5GpeZAXfr4yh3Yih3MRgJm75Uxg/edit#gid=0";
  var book = my_app.openByUrl(url);
  var sheet = book.getSheetByName("Sheet1");
  var new_sheet = book.getSheetByName(start.toString());
  if (new_sheet == null) {
    var new_sheet = book.insertSheet(start.toString())
  }

  var line_num = 0;
  var email_count = 0;
  var email_subject = "SCSN ANSWER SHEET";
  var email_sender = MailApp;
  var email_cc = ""; // regina.ayodele@gmail.com
  //ssn_clean = ssn.toString().trim().replaceAll(',',"").replaceAll(' ', "").replaceAll("-", "").replaceAll("(", "").replaceAll(")", "").replaceAll(".","");
  //ssn = ssn_clean.length < 9 ? padLeadingZeros(ssn_clean, 9) : ssn_clean;

  //let ssn_col = 4;
  var cpr_a = ['d', 'c', 'b', 'c', 'a', 'd', 'b', 'c', 'b', 'd', 'a', 'd', 'a', 'a', 'c', 'a', 'c', 'b', 'a', 'b', 'a', 'c', 'c', 'd', 'd'];
  var cpr_b = ['a', 'c', 'b', 'a', 'd', 'd', 'b', 'c', 'b', 'd', 'a', 'c', 'a', 'c', 'b', 'd', 'c', 'b', 'a', 'd', 'a', 'c', 'd', 'a', 'b'];
  var f_aid_b = ['d', 'd', 'c', 'b', 'a', 'b', 'c', 'b', 'b', 'd', 'c', 'd', 'd', 'c', 'd'];
  var ans_count = 0;
  var score = 100;

  for (let i = 0; i < cpr_a.length; i++) {
    if (version == "A") {
      if (cpr_a[i] !== ans[i]) {
        ans_count++;
      }
    } else {
      if (cpr_b[i] !== ans[i]) {
        ans_count++;
      }
    }
  }

  //console.log(ans_count);
  var score = ((25 - ans_count)/25)*100;
  var msg = "Thank you " + fname +". Your answers have been recorded. Please review your score: " + score.toFixed(2) +  "/100";
  var email_msg = "Thank you, " + fname + ", your answers have been recorded. Please review your score: " + score.toFixed(2) +  "/100. " + ans.toString();
  var last_row = new_sheet.getLastRow();

  if (last_row < 1) {
    new_sheet.appendRow(["Timestamp", "First",	"Last",	"Phone",	"Email", "", "Version", "Answers", "Notice", "Quota"]);
    var last_row = new_sheet.getLastRow();
    var last_col = new_sheet.getLastColumn();
    new_sheet.getRange(1, 1, last_row, last_col + 1).setFontWeight("bold");
    new_sheet.appendRow([timestamp, fname.trim(), lname.trim(), phone, email, "", version, score.toFixed(2) + " | " + ans.toString()]);
    var last_row = new_sheet.getLastRow();
    //new_sheet.getRange(last_row, ssn_col).setNumberFormat('@STRING@');
  } else {
    new_sheet.appendRow([timestamp, fname.trim(), lname.trim(), phone, email, "", version, score.toFixed(2) + " | " + ans.toString()]);
    var last_row = new_sheet.getLastRow();
    //new_sheet.getRange(last_row, ssn_col).setNumberFormat('@STRING@');
  }

  try {
    twilio_sms(phone.toString(), msg);
    new_sheet.getRange(last_row, 9).setValue("sent");
  } catch (e) {
    twilio_sms("6028248091", "Error in Enrollment Conf: " + e.toString().substring(0, 133));
  }

  try {
    new_sheet.getRange(last_row, 9).setValue("sent");
    new_sheet.getRange(last_row, 10).setValue(sendEmail(email, email_subject, email_msg, "Star Canyon School", email_cc, [])); // Use sendEmail call to set quota
    new_sheet.getRange(last_row, 10).setValue(email_sender.getRemainingDailyQuota()); 
  } catch (e) {
    twilio_sms("6028248091", "Error in Enrollment Conf: " + e.toString().substring(0, 133));
  }  

  for (row = 2; row <= last_row; row++) { 
    // Notice column is O/13
    if (new_sheet.getRange(row, 9).getValue() != "sent") {
      //ssn = sheet.getRange(row, ssn_col).getValue();
      //ssn_clean = ssn.toString().trim().replaceAll(',',"").replaceAll(' ', "").replaceAll("-", "").replaceAll("(", "").replaceAll(")", "").replaceAll(".","");
      //ssn = ssn_clean.length < 9 ? padLeadingZeros(ssn_clean, 9) : ssn_clean;
      //new_sheet.getRange(row, ssn_col).setNumberFormat('@STRING@');
      try {
        new_sheet.getRange(row, 10).setValue(sendEmail(email, email_subject, email_msg, "Star Canyon School", email_cc, [])); // Use sendEmail call to set quota
        new_sheet.getRange(row, 9).setValue("sent");
      } catch (e) {
        twilio_sms("6028248091", "Error in Enrollment Conf: " + e.toString().substring(0, 133));
      }
      try {
        twilio_sms(phone.toString(), msg);
        twilio_sms("6028248091", msg);
        new_sheet.getRange(row, 9).setValue("sent");
        new_sheet.getRange(row, 10).setValue(email_sender.getRemainingDailyQuota());
      } catch (e) {
        twilio_sms("6028248091", "Error in Enrollment Conf: " + e.toString().substring(0, 133));
      }
    }      
    line_num = line_num + 1
    email_count++
  }
  Logger.log("Notices sent: " + line_num);
}

function padLeadingZeros(num, size) {
    var s = num+"";
    while (s.length < size) s = "0" + s;
    return s;
}

