function sendEmail(email_addr, subject, msg, sender, copy, attachments) {
  // if attachments is array then pass as array, otherwise convert to array
  attached = Array.isArray(attachments) ? attachments : [attachments];
  GmailApp.sendEmail(email_addr, subject, msg, {
    attachments: attached,
    name: sender,
    cc: copy
  });
  return MailApp.getRemainingDailyQuota();
}

function pdf_maker(firstName, lastName, course_date, course_time) {

  //Doc template: 14Pw62SGpW2Wq01GMgKx7cya0YoPQMwcAF2W5gBBCHoc
  //Doc folder: 1vBGvJXCyRlX0T-Wjsp5LcEPWV5nuESwJ
