  let doc_template = DriveApp.getFileById("14Pw62SGpW2Wq01GMgKx7cya0YoPQMwcAF2W5gBBCHoc");
  let pdf_folder = DriveApp.getFolderById("1vBGvJXCyRlX0T-Wjsp5LcEPWV5nuESwJ");
  //let pdf_folder = doc_folder;
  let temp_file = doc_template.makeCopy(pdf_folder);
